    // }

    return (
        <div>

            {subject.courses.map((course) => {
                 return (
                    <button
                    type="button"
                        onClick={()=>setCourse(course)}
                        name={course}
                        value={course}> {course.name}</button>
                );
            })}

        </div>
    );
}

export default Courses;
