import React, { useState } from "react";

function Courses({ subject }) {
    const [currentCourse, setCourse] = useState('test')





    const handleClick = (e) => {
        e.preventDefault()
        console.log(e.target.name)
        setCourse(e.target.name)
        console.log(currentCourse)


    }

    return (
        <div>

            {subject.courses.map((course) => {
                 return (

                    <button
                    type="button"
                    name={course.name}
                    value={currentCourse}
                    onClick={handleClick}

                    > 
                    {course.name}
                    </button>
                );
            })}


        </div>
    );
}

export default Courses;
