import "./SubjectCard.css"
import Courses from './Courses'

function SubjectCard({subject, courses}) {
const [show, setShow] = useState(false)


    const name = courses.map(course => {
        return course.name
    })
    const handleClick = () => {

        const bool = !show
        setShow(bool)



    }

    return (

        <div className = "subject__card" onClick={()=>handleClick(subject)} >
            <h2>{subject.name}</h2>
            {show?  <Courses subject={subject}/> : null}

        </div>

    )
}

export default SubjectCard
