import React, { useState } from "react";

function Courses({ subject }) {
    const [currentCourse, setCourse] = useState("")
    const [state, setstate] = useState("bob")


    console.log(state);

    const handleClick = (e) => {
        setstate(e.target.name)
    }

    return (
        <div>

            {subject.courses.map((course) => {
                 return (
                    <button
                    type="button"
                    name={course.name}
                    value={currentCourse}
                    onClick={handleClick}

                    > 
                    {course.name}
                    </button>
                );
            })}

        </div>
    );
}

export default Courses;
